// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract PayItForwardToken is ERC20, Ownable {
    constructor() ERC20("PayItForwardToken", "PFT") Ownable() {}

    struct Stake {
        uint256 amount;
        uint256 startTime;
        uint256 duration;
    }

    struct ActOfKindness {
        address initiator;
        address recipient;
        string description;
        uint256 timestamp;
        bool verified;
        uint256 reward;
    }

    mapping(address => uint256) public balances;
    mapping(address => Stake) public stakes;
    mapping(address => bool) public initializedWallets;
    mapping(address => uint256) public reputationScores;
    mapping(uint256 => ActOfKindness) public actsOfKindness;
    uint256 public actCounter;

    uint256 public constant BASE_REWARD = 10 ether;
    uint256 public constant VERIFICATION_THRESHOLD = 3;
    uint256 public constant REPUTATION_INCREMENT = 10;

    event WalletInitialized(address indexed user, uint256 amount);
    event TokensTransferred(address indexed from, address indexed to, uint256 amount);
    event TokensStaked(address indexed user, uint256 amount, uint256 duration);
    event ActOfKindnessRecorded(
        uint256 indexed actId,
        address indexed initiator,
        address indexed recipient,
        string description,
        uint256 reward
    );
    event ActVerified(uint256 indexed actId, address verifier);
    event ReputationUpdated(address indexed user, uint256 newScore);


    function initializeWallet(address user, uint256 initialTokens) external onlyOwner {
        require(!initializedWallets[user], "Wallet already initialized");
        _mint(user, initialTokens);
        initializedWallets[user] = true;
        emit WalletInitialized(user, initialTokens);
    }

    function transfer(address to, uint256 amount) public override returns (bool) {
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");
        _transfer(msg.sender, to, amount);
        emit TokensTransferred(msg.sender, to, amount);
        return true;
    }

    function stakeTokens(uint256 amount, uint256 duration) external {
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");

        _transfer(msg.sender, address(this), amount);
        stakes[msg.sender] = Stake({
            amount: amount,
            startTime: block.timestamp,
            duration: duration
        });

        emit TokensStaked(msg.sender, amount, duration);
    }

    function unstakeTokens() external {
        Stake memory stake = stakes[msg.sender];
        require(stake.amount > 0, "No staked tokens");
        require(block.timestamp >= stake.startTime + stake.duration, "Staking period not ended");

        _transfer(address(this), msg.sender, stake.amount);
        delete stakes[msg.sender];
    }

    function balanceOf(address account) public view override returns (uint256) {
        return super.balanceOf(account);
    }

    function recordActOfKindness(
        address recipient,
        string memory description
    ) external {
        require(initializedWallets[msg.sender], "Wallet not initialized");
        require(initializedWallets[recipient], "Recipient wallet not initialized");

        actCounter++;
        actsOfKindness[actCounter] = ActOfKindness({
            initiator: msg.sender,
            recipient: recipient,
            description: description,
            timestamp: block.timestamp,
            verified: false,
            reward: 0
        });

        emit ActOfKindnessRecorded(actCounter, msg.sender, recipient, description, 0);
    }

    function verifyActOfKindness(uint256 actId) external {
        require(initializedWallets[msg.sender], "Wallet not initialized");
        ActOfKindness storage act = actsOfKindness[actId];
        require(act.initiator != address(0), "Act does not exist");
        require(!act.verified, "Act already verified");
        require(msg.sender != act.initiator, "Cannot verify your own act");

        // Temporary storage for verification count
        uint256 verificationCount = 0;

        // Check if act should be verified (simplified for now)
        if (verificationCount >= VERIFICATION_THRESHOLD) {
            act.verified = true;
            act.reward = BASE_REWARD;

            // Distribute rewards
            _mint(act.initiator, act.reward);
            _mint(act.recipient, act.reward / 2);

            // Update reputation
            reputationScores[act.initiator] += REPUTATION_INCREMENT;
            reputationScores[act.recipient] += REPUTATION_INCREMENT / 2;

            emit ReputationUpdated(act.initiator, reputationScores[act.initiator]);
            emit ReputationUpdated(act.recipient, reputationScores[act.recipient]);
        }

        emit ActVerified(actId, msg.sender);
    }

    function getReputationScore(address user) external view returns (uint256) {
        return reputationScores[user];
    }

    function getActDetails(uint256 actId) external view returns (
        address initiator,
        address recipient,
        string memory description,
        uint256 timestamp,
        bool verified,
        uint256 reward
    ) {
        ActOfKindness memory act = actsOfKindness[actId];
        return (
            act.initiator,
            act.recipient,
            act.description,
            act.timestamp,
            act.verified,
            act.reward
        );
    }
}
